let tools = {
    add: (...numbers) => { 
        let sum = 0;
        for (let index in numbers) {
            sum += numbers[index]
        }
        return sum;
    },
    multiply: (...numbers) => {
        let sum = numbers[0];
        for (let index in numbers) {
            sum = sum * numbers[index]
        }
        return sum;
    }
}

module.exports = tools

